package Lec_3;
import java.util.*;
public class EvenOrOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     Scanner s= new Scanner(System.in);
     int a= s.nextInt();
     if( a%2 == 0){
    	 // Yes 
    	 System.out.println("a is Even");
     }else {
    	 // No
    	 System.out.println("a is Odd");
     }
     
     
     
     
     
     
	}

}
