package Lec_4;
import java.util.*;
public class pattern_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       Scanner s= new Scanner(System.in);
       
       int n= s.nextInt();
       
       int i=1;
       while(i<=n) {
    	   System.out.print("*");
    	   i=i+1;
       }
       
	}

}
