package Revision;
import java.util.*;
public class Print_hello_world {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      
	   Scanner s= new Scanner(System.in);
	   int n=s.nextInt();
	   
	   int i=1; // initalization
	   while(i<=n) {
		   
		   System.out.print("*");
		   i=i+1;// re-ini
	   }
	   
	   
	   
	   
	}

}
